import React, { useEffect, useState } from 'react'
import styled from 'styled-components';
import { Typography, Table, Image, Tag, Progress, DatePicker, Space, TimePicker, Select } from 'antd'

import { columns, data } from './config';

const { Title, Text } = Typography;
const { Option } = Select;

function ImageVideo() {
    const newData = data.map((i, index) => ({
        ...i,
        image_video: i.type === 'image' ?
            <Image
                width={230}
                height={150}
                src={i.image_video || "error"}
                fallback="data:image/png;base64,"
            /> :
            i.image_video
    }));

    const [imageVideos, setImageVideos] = useState(newData);
    const [filterFields, setFilterFields] = useState({
        createdAtDate: null,
        createdAtTime: null,
        status: null,
        droneId: null
    })

    const onChangeDate = (date, dateString) => {
        console.log(date, dateString);
        setFilterFields({ ...filterFields, createdAtDate: dateString });
    }

    const onChangeTime = (time, timeString) => {
        console.log(time, timeString);
        setFilterFields({ ...filterFields, createdAtTime: timeString });

    };

    const onChangeStatus = (status) => {
        console.log("status: ", status);
        setFilterFields({ ...filterFields, status });
    };

    const onChangeDrone = (droneId) => {
        console.log("droneId: ", droneId);
        setFilterFields({ ...filterFields, droneId })
    };

    const onChangeProblem = (problemId) => {
        console.log("problemId: ", problemId);
        setFilterFields({ ...filterFields, problemId })
    };

    useEffect(() => {
        let result = [...newData];
        Object.keys(filterFields).forEach(filterField => {
            if (![null, undefined, ""].includes(filterFields[filterField])) {
                result = result.filter(i => i[filterField] === filterFields[filterField]);
            }
        });
        setImageVideos(result);
    }, [filterFields])

    return (
        <Container>
            <TitleCustom level={2}>Quản lý hình ảnh, video</TitleCustom>
            <SpaceCustom>
                <Space direction="horizontal">
                    <Text>Ngày:</Text>
                    <DatePicker placeholder="Chọn ngày" onChange={onChangeDate} />
                </Space>
                <Space direction="horizontal">
                    <Text>Giờ:</Text>
                    <TimePicker placeholder="Chọn giờ" onChange={onChangeTime} />
                </Space>
                <Space direction="horizontal">
                    <Text>Trạng thái sự cố:</Text>
                    <Select placeholder="Chọn trạng thái" style={{ minWidth: 120 }} allowClear onChange={onChangeStatus}>
                        <Option value={false}>Không có sự cố</Option>
                        <Option value={true}>Có sự cố</Option>
                    </Select>
                </Space>
                <Space direction="horizontal">
                    <Text>Drones:</Text>
                    <Select placeholder="Chọn drone" style={{ minWidth: 100 }} allowClear onChange={onChangeDrone}>
                        {[... new Set(newData.map(i => i.droneId))].map(droneId => <Option value={droneId}>{droneId}</Option>)}
                    </Select>
                </Space>
                <Space direction="horizontal">
                    <Text>Sự cố:</Text>
                    <Select placeholder="Chọn sự cố" style={{ minWidth: 100 }} allowClear onChange={onChangeProblem}>
                        {[... new Set(newData.map(i => i.problemId))].map(problemId => <Option value={problemId}>{problemId}</Option>)}
                    </Select>
                </Space>
            </SpaceCustom>
            <Table columns={columns} dataSource={imageVideos} onChange={() => { }} />
        </Container>
    )
}

const Container = styled.div``;

const TitleCustom = styled(Title)`
    text-align: center;
    padding: 10px 0;
`;

const SpaceCustom = styled(Space)`
    margin-bottom: 10px;
`;

export default ImageVideo;